/* Function to open sidebar menu */
function openNav() {
    document.getElementById("mySidenav").style.width = "300px";
    
    /* Transition to load left to right - super slow */
    var left = $('.sidebar-div a').offset().left;
    $(".sidebar-div a").css({left}).animate({"left":"20px"}, 50);
}

function closeNav() {
    document.getElementById("mySidenav").style.width = "0"; 
    var left = $('.sidebar-div a').offset().left;
    $(".sidebar-div a").css({left:left}).animate({"left":"0px"}, 50);
}


/* Transition of logo when scrolling - buggy */
$(document).ready(function(){
    x = 200;
     $(function(){
	 var lastScrollTop = 0, delta = 5;
         
	 $(window).scroll(function(){
		 var nowScrollTop = $(this).scrollTop();
		 if(Math.abs(lastScrollTop - nowScrollTop) >= delta){
		 	if (nowScrollTop > lastScrollTop){
                if(x > 0){
               $("#header-logo").width(x-=20);
               $("#mklogo").css('padding-left', "85px");
                }
		 	} else {
                if(x < 200){
               $("#header-logo").width(x+=25);}
                $("#mklogo").css('padding-left', "0px");

			}
		 lastScrollTop = nowScrollTop;
		 }
	 });
 });
});
